function openNav(){const screenWidth=window.innerWidth;const bg=document.querySelector('.bg');if(screenWidth<576){document.getElementById("mySidenav").style.width="100vw";document.getElementById("main").style.marginLeft="0";document.getElementById("mySidenav").classList.add('text-center');}else{document.getElementById("mySidenav").style.width="250px";document.getElementById("main").style.marginLeft="250px";bg.style.zIndex="20";bg.style.backgroundColor="rgba(0,0,0,0.4)";document.body.style.overflow='hidden';}}
function closeNav(){const bg=document.querySelector('.bg');document.getElementById("mySidenav").style.width="0";document.getElementById("main").style.marginLeft="0";document.body.style.backgroundColor="white";bg.style.zIndex="1";bg.style.display="none";}
var modal=document.getElementById("myModal");var btn=document.getElementById("myBtn");var span=document.getElementsByClassName("close")[0];btn.onclick=function(){modal.style.display="block";}
span.onclick=function(){modal.style.display="none";}
window.onclick=function(event){if(event.target==modal){modal.style.display="none";}}